import React from "react";

import { productCards } from "../../utils/Constant";
import Card1 from "./Card1";

const Card = () => {
  return (
    <div className="card h-auto w-full rounded-md inline-flex flex-row">
      <div className=" card1 ring-1 bg-[#E26D5A] rounded-xl inline-flex  flex-row ">
        <div className="inline-flex justify-between rounded-full flex-col">
          <div className="inline-flex flex-row justify-between items-center m-[10px] text-xl">
            <span>Deal of of The Day</span>
            <button className="shadow m-[2rem]">View All</button>
          </div>
          <div className="card11 inline-flex ">
            <div className="grid gap-4 grid-cols-3 grid-rows-2 rounded-xl">
              {productCards.map((k, i) => {
                return <Card1 key={i} src={k.image} text="Tata Steel TMT" />;
              })}
            </div>
          </div>
        </div>
      </div>
      <div className=" card2 inline-flex h-auto w-auto shadow ">
        <div className=" card3 "></div>
        <div className="card4 "></div>
      </div>
    </div>
  );
};

export default Card;
