import React from "react";

import { productCards ,Quote} from "../../utils/Constant";
import Card1 from "./Card1";
import {qoute1 ,quote2 , quote3} from "../../utils/Icon";

const Card = () => {
  return (
    <div className="card h-auto w-full rounded-md inline-flex flex-row m-[10px] p-[20px]">
      <div className=" card1 ring-1 bg-gradient-to-r from-orange-500 to-orange-0 rounded-xl inline-flex w-[650px] flex-row ">
        <div className="inline-flex justify-between rounded-full flex-col ">
          <div className="inline-flex flex-row justify-between items-center p-[10px] text-xl">
            <span className="ml-[4rem] text-white pt-[2rem]">Deal of of The Day</span>
            <button className="shadow m-[10px] rounded-xl text-white bg-[#D9D9D980]">View All</button>
          </div>
          <div className="card11 inline-flex bg-[#D9D9D9] m-[30px] p-[20px] rounded-xl">
            <div className="w-full grid gap-4 grid-cols-3 grid-rows-2 bg-white m-[10px] rounded-xl ">

              {productCards.map((k, i) => {
                return <Card1 classname="shadow-xl " key={i} src={k.image} text="Tata Steel TMT" />;
              })}

          </div>
          </div>
        </div>
      </div>
     <div className=" card2 inline-flex flex-row w-[382px] ml-[4rem] h-[339px] shadow bg-[#A6F3F3] bg-gradient-to-r from-blue-500 to-blue-100 rounded-xl "> 
      <div className=" inline-flex flex-col h-[339px] w-full shadow ">
        <div className=" card3  w-full">
            <div className="inline-flex flex-col items-align  ">
                <span className="text-xl mt-[3rem] ml-[32px] justify-start">Get A Quote</span>
                <span className="ml-[30px] ">Get an approximation in 3 easy steps</span>
                <div className="inline-flex flex-col">
            {Quote.map((k,i)=>{
                return <div clasName="inline-flex flex-row ">
                    <div>{k.icon}</div>
                    <div>{k.text}</div>
                </div>
            })}</div>

                {/* <span className="">Enter Requirnments</span> */}
                {/* <span className="">Enter Location</span> */}
                {/* <span className="">Get quotation</span> */}
                <button>Get A Quote </button>
            </div>

        </div>
        <div className="card4 "></div>
      </div>
    </div>
 </div>
  );
};

export default Card;
