import React from "react";

import { productCards, Quote } from "../../utils/Constant";
import Card1 from "./Card1";
import { qoute1, quote2, quote3 } from "../../utils/Icon";

const Card = () => {
  return (
    <div className="card h-auto w-full rounded-md inline-flex flex-row m-[10px] p-[20px]">
      <div className=" card1 ring-1 bg-gradient-to-r from-orange-500 to-orange-0 rounded-xl inline-flex w-[650px] flex-row ">
        <div className="inline-flex justify-between rounded-full flex-col ">
          <div className="inline-flex flex-row justify-between items-center p-[10px] text-xl">
            <span className="ml-[4rem] text-white pt-[2rem]">
              Deal of of The Day
            </span>
            <button className="shadow m-[10px] rounded-xl text-white bg-[#D9D9D980]">
              View All
            </button>
          </div>
          <div className="card11 grid gap-4 grid-cols-3 grid-rows-2 inline-flex bg-[#D9D9D9] m-[30px] p-[20px] rounded-xl">
            {productCards.map((k, i) => {
              return (
                <Card1
                  className="shadow-xl bg-white "
                  key={i}
                  src={k.image}
                  text="Tata Steel TMT"
                />
              );
            })}
          </div>
        </div>
      </div>
      <div className=" card2 inline-flex flex-col  w-[382px] ml-[4rem] h-[339px] shadow bg-[#A6F3F3] bg-gradient-to-r from-blue-500 to-blue-100 rounded-xl ">
        <div className=" inline-flex flex-col h-[339px] w-full shadow ">
          <div className=" card3  w-full">
            <div className="inline-flex flex-col items-align  ">
              <span className="text-xl mt-[3rem] ml-[32px] justify-start">
                Get A Quote !
              </span>
              <span className="ml-[30px] ">
                Get an approximation in 3 easy steps
              </span>
              <div className="inline-flex flex-col w-full space-y-3 mt-[20px] ml-[2rem]">
                {Quote.map((k, i) => {
                  return (
                    <div clasName="inline-flex flex-row align-start w-full ">
                      <div className="inline-flex w-full items-center  space-x-5">
                        <div className="rounded-full bg-white h-[20px] w-[20px] ring-1 inline-flex items-center space-y-5 justify-center">
                          <span className="text-center">{i + 1}</span>
                        </div>
                        <span>{k.text}</span>
                      </div>
                    </div>
                  );
                })}
              </div>
              <button className="ring-1 bg-white rounded-full p-[10px] mt-[2rem] mb-[2rem] ml-[4rem]">
                Get A Quote{" "}
              </button>
            </div>
          </div>
        </div>
        <div className=" inline-flex flex-col h-[200px] w-full shadow ">
          <div className=" card3  w-full">
            <div className="inline-flex flex-col items-align  p-[12px] ">
              <span className="text-xl justify-start">
                Join Us!
              </span>
              <span className="ml-[30px] ">
                Get discovered by buisness buyers.
              </span>
              <span>Expand ur reach and boost ur sales. </span>

              <button className="ring-1 bg-white rounded-full p-[10px] mt-[2rem] mb-[2rem] ml-[4rem]">
                Start selling now
              </button>
            </div>
          </div>
        </div>
        </div>
      </div>
  );
};

export default Card;
