import React from "react";

import { truck, chemicals, bricks, flyash, sands } from "../../utils/Icon";

import { frame } from "../../utils/Constant";

const Frame = () => {
  return (
    <div className="Frame h-[520px] w-full px-[2rem] pb-[2rem] bg-[#E5E5E5]; ">
      <div className="grid gap-4 grid-cols-4 grid-rows-2 ">
        {frame.map((k, i) => {
          let bg = "bg-white";
          let shadow = "shadow";
          let textSize ="text-[28px]";

          if (i == 0) {
            bg = "bg-transparent";
            shadow = ""
            textSize="text-[50px]";
          }

          return (
            <div
              className={`${shadow} inline-flex items-center rounded-lg ${bg} justify-start ml-[1rem] mt-[2rem] relative min-h-[150px] w-auto`}
            >
              <span
                className={`text-black ${textSize} align-center px-[]`}
                key={i}
              >
                {k.title}
              </span>

              <span className="absolute bottom-0 right-0" key={i}>
                {k.image}
              </span>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Frame;
