import React from "react";

import { productCards } from "../../utils/Constant";
import Card1 from "./Card1";

const Card = () => {
  return (
    <div className="card h-auto w-full rounded-md inline-flex flex-row m-[10px] p-[20px]">
      <div className=" card1 ring-1 bg-gradient-to-r from-orange-500 to-orange-0 rounded-xl inline-flex  flex-row ">
        <div className="inline-flex justify-between rounded-full flex-col ">
          <div className="inline-flex flex-row justify-between items-center p-[10px] text-xl">
            <span className="ml-[4rem] text-white pt-[2rem]">Deal of of The Day</span>
            <button className="shadow m-[10px] rounded-xl text-white bg-[#D9D9D980]">View All</button>
          </div>
          <div className="card11 inline-flex bg-[#D9D9D9] m-[30px] p-[20px] rounded-xl">
            <div className="w-full grid gap-4 grid-cols-3 grid-rows-2 bg-white m-[10px] rounded-xl ">

              {productCards.map((k, i) => {
                return <Card1 classname="shadow-xl " key={i} src={k.image} text="Tata Steel TMT" />;
              })}

          </div>
          </div>
        </div>
      </div>
     <div className=" card2 inline-flex flex-row  shadow "> 
      <div className=" inline-flex flex-col h-auto w-full shadow ">
        <div className=" card3  ">
            <div className="inline-flex flex-col items-center m-50px">
                <span>Get A Quote</span>
                <span>Get an approximation in 3 easy steps</span>
            </div>

        </div>
        <div className="card4 "></div>
      </div>
    </div>
 </div>
  );
};

export default Card;
